import { useState } from "react";

const Form = () => {
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        email: '',
        comments: '',
        isFriendly: true
    });

    const handleSubmit = e => {
        e.preventDefault();
    }

    const handleChange = e => {
        const {name, value, type, checked} = e.target;
        setFormData(prevFormData => {
            return {
                ...prevFormData, 
                [name]: type === 'checkbox' ? checked : value
            }
        });
        console.log(formData);
    }

    return ( 
        <>
            <h1 className="my-4 text-xl uppercase">React Forms</h1>
            <form className="mt-4" onSubmit={handleSubmit}>
                <label>
                    <span>First Name:</span>
                    <input 
                        type="text" 
                        placeholder="First Name" className="border border-black px-2 py-1 mb-3" 
                        name="firstName"
                        value={formData.firstName}
                        onChange={handleChange}
                    />
                </label>
                <br />
                <label>
                    <span>Last Name:</span>
                    <input 
                        type="text" 
                        placeholder="Last Name" 
                        className="border border-black px-2 py-1 mb-3"
                        name="lastName"
                        value={formData.lastName}
                        onChange={handleChange}
                 />
                </label>
                <br />
                <label>
                    <span>Email:</span>
                    <input 
                        type="email" 
                        placeholder="Last Name" 
                        className="border border-black px-2 py-1 mb-3"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                 />
                </label>
                <br />
                <label>
                    <span>Comments:</span>
                    <br />
                    <textarea 
                        className="border border-black h-20"
                        placeholder="Comments"
                        name="comments"  
                        value={formData.comments} 
                        onChange={handleChange}
                    />
                </label>
                <br />
                <label>
                    <span>Are you friendly?</span>
                    <input 
                        type="checkbox"
                        checked={formData.isFriendly ? true : false}
                        name="isFriendly"
                        onChange={handleChange}
                    />
                </label>
                <br />
                <fieldset>
                    <legend>Current employment status</legend>
                </fieldset>
                <button className="px-2 py-2 border border-black">Submit</button>
                <div>
                    <p>First Name: {formData.firstName}</p>
                    <p>Last Name: {formData.lastName}</p>
                    <p>Email: {formData.email}</p>
                    <p>Comments: {formData.comments}</p>
                </div>
            </form>
        </>
    );
}

export default Form;
