import memes from "../shareable/memesData";
import { useState } from "react";

const Example = () => {
    const [url, setUrl] = useState('');
    const [topText, setTopText] = useState('');
    const [bottomText, setBottomText] = useState('');

    const handleClick = () => {
        const randomNum = Math.floor(Math.random() * memes.length);
        setUrl(memes[randomNum].url);
    }
    return ( 
        <div>
            <input type="text" placeholder="Top Text" value={topText} />
            <input type="text" placeholder="Bottom Text" value={bottomText} />
            <br />
            <button onClick={handleClick}>Get Meme</button>
            <br />
            <img src={url} alt="" />
        </div>
    );
}

export default Example;
