import { useEffect } from "react"

const WindowTracker = () => {

    useEffect(() => {
        window.addEventListener('resize', () => {
            console.log('resized')
        })
    }, [])
    return ( 
        <>
            <h2>Window width: {window.innerWidth}</h2>
        </>
     );
}

export default WindowTracker;
