import { useState } from "react";
import boxData from "../shareable/boxes";
import Box from "./Box";

const Boxes = () => {
    const [squares, setSquares] = useState(boxData);
    const [on, setOn] = useState();

    const handleClick = (id) => {
        const box = squares[id];
        console.log(squares[id].on);
        squares[id].on = !squares[id].on;
        console.log(squares[id].on);
    }
    return ( 
        <div className="grid grid-cols-3 gap-5">
            {
                squares.map(box => <Box key={box.id} box={box} handleClick={handleClick} />)
            }
        </div>
    );
}

export default Boxes;
