import { useState } from "react";

const Form = () => {
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        email: '',
        comments: ''
    });

    const handleSubmit = e => {
        e.preventDefault();
    }

    const handleChange = e => {
        setFormData(prevFormData => {
            return {
                ...prevFormData, 
                [e.target.name]: e.target.value
            }
        });
        console.log(formData);
    }

    return ( 
        <>
            <h1 className="my-4 text-xl uppercase">React Forms</h1>
            <form className="mt-4" onSubmit={handleSubmit}>
                <label>
                    <span>First Name:</span>
                    <input 
                        type="text" 
                        placeholder="First Name" className="border border-black px-2 py-1 mb-3" 
                        name="firstName"
                        value={formData.firstName}
                        onChange={handleChange}
                    />
                </label>
                <br />
                <label>
                    <span>Last Name:</span>
                    <input 
                        type="text" 
                        placeholder="Last Name" 
                        className="border border-black px-2 py-1 mb-3"
                        name="lastName"
                        value={formData.lastName}
                        onChange={handleChange}
                 />
                </label>
                <br />
                <label>
                    <span>Email:</span>
                    <input 
                        type="email" 
                        placeholder="Last Name" 
                        className="border border-black px-2 py-1 mb-3"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                 />
                </label>
                <br />
                <label>
                    <span>Email:</span>
                    <textarea 
                        cols="30" 
                        rows="10"
                        name="comments"  
                        value={formData.comments} 
                        onChange={handleChange}
                    />
                </label>
                <br />
                <button className="px-2 py-2 border border-black">Submit</button>
                <div>
                    <p>First Name: {formData.firstName}</p>
                    <p>Last Name: {formData.lastName}</p>
                    <p>Email: {formData.email}</p>
                </div>
            </form>
        </>
    );
}

export default Form;
