import memes from "../shareable/memesData";
import { useState } from "react";


const Meme = () => {
    const [url, setUrl] = useState(memes[0].url);
    const [count, setCount] = useState(0);
    const [isGoingOut, setIsGoingOut] = useState(false);

    const handleClick = () => {
        const randomNum = Math.floor(Math.random() * memes.length);
        const url = memes[randomNum].url;
        setUrl(url);
    }

    const add = () => {
        setCount(prevCount => prevCount + 1);
    }

    const remove = () => {
        setCount(prevCount => prevCount - 1);
    }

    return ( 
        <main>
            <div>
                <input type="text" placeholder="Top text" />
                <input type="text" placeholder="Bottom text" />
                <br />
                <br />
                <button onClick={handleClick}>Get a new meme image</button>
            </div>
            <img src={url} alt="Meme image" style={{ maxWidth: '100%' }} />
            <hr />
            <div>{count}</div>
            <button onClick={add}>+</button>
            <button onClick={remove}>-</button>
            {isGoingOut ? <p>Hello World</p> : <p>Not Going Out</p>}
            <button onClick={() => setIsGoingOut(!isGoingOut)}>Toggle Is Going Out</button>
        </main>
    );
}

export default Meme;
